"use strict";

let view = new (require('dojs-template'))

class Controller {

    constructor(ctrl){
        this.name = ctrl + '/'
    }

    //定义一个变量，类似于smarty，把该
    assign(key, val){
        key += ''
        if(!key || empty(val))
            return

        view.assign(key, val)
    }

    //模板渲染, 参数是模板名, 可不带后缀, 默认是 .tpl
    render(file){
        view.render(VIEWS + file, html => {
            Response.render(html)
        }).catch(err => {
            Response.error(err)
        })
    }

    //解决js回调恶梦
    async(fn){
        if(typeof fn !== 'function')
            return Response.error('action not found', 404)
        
        return libs.CO.call(this, fn)
    }
}


module.exports = Controller