"use strict";


class Router{

    constructor(){
        
    }

    /**
     * [init 启动路由]
     */
    init(){
   
        try{
            let App = require(APPS + Request.app)

            if(App){
                if(Request.path.length < 1)
                    Request.path.push('index')

                var act = Request.path.shift()

                if(App.prototype[act]){
                    let _app = new App(Request.app)
                    return _app[act].apply(_app, Request.path)
                }
            }
            Response.error('Action not found', 404)
        }catch(err){
            Response.error(`Module [${Request.app}] not found ${err.stack}`, 404)
        }
        
    }


}


module.exports = Router